// Enhanced error processing with tenant context
const input = $input.item.json;

// Extract tenant context from various sources
const extractTenantId = () => {
  // From direct input
  if (input.tenant_id) return input.tenant_id;

  // From headers
  if (input.headers && input.headers['x-tenant-id']) return input.headers['x-tenant-id'];

  // From user context (need to lookup)
  if (input.user_id) return 'lookup_required';

  return null;
};

// Classify error severity and type
const classifyError = (error, severity) => {
  const errorText = (error || '').toLowerCase();

  // Critical patterns
  if (errorText.includes('database') && errorText.includes('connection')) {
    return { severity: 'critical', category: 'infrastructure', escalate: true };
  }
  if (errorText.includes('payment') || errorText.includes('billing')) {
    return { severity: 'high', category: 'financial', escalate: true };
  }
  if (errorText.includes('security') || errorText.includes('unauthorized')) {
    return { severity: 'critical', category: 'security', escalate: true };
  }
  if (errorText.includes('agent') && errorText.includes('failed')) {
    return { severity: 'high', category: 'agent_failure', escalate: false };
  }

  // Use provided severity or default
  const sev = severity || 'medium';
  return { 
    severity: sev, 
    category: 'general', 
    escalate: sev === 'critical' || sev === 'high' 
  };
};

const tenantId = extractTenantId();
const errorClassification = classifyError(input.error, input.severity);

// Create comprehensive error data
const errorData = {
  tenant_id: tenantId,
  user_id: input.user_id || null,
  error_message: input.error || 'Unknown error',
  severity: errorClassification.severity,
  category: errorClassification.category,
  context: input.context || 'Unknown context',
  source: input.source || 'System',
  stack_trace: input.stack || input.stackTrace || null,
  request_id: input.request_id || null,
  session_id: input.session_id || null,
  ip_address: input.ip_address || input.headers?.['x-forwarded-for'] || 'unknown',
  user_agent: input.user_agent || input.headers?.['user-agent'] || 'unknown',
  timestamp: new Date().toISOString(),
  alert_id: `alert-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  escalate: errorClassification.escalate,
  tenant_lookup_required: tenantId === 'lookup_required',
  metadata: {
    workflow_execution_id: $execution.id,
    n8n_workflow_id: $workflow.id,
    environment: input.environment || 'production',
    version: input.app_version || 'unknown'
  }
};

return { json: errorData };

// Merge tenant information
const errorData = $node['Process & Classify Error'].json;
const tenantInfo = $node['Lookup Tenant']?.json?.[0];

if (tenantInfo) {
  errorData.tenant_id = tenantInfo.tenant_id;
  errorData.tenant_name = tenantInfo.tenant_name;
  errorData.tenant_slug = tenantInfo.tenant_slug;
  errorData.user_role = tenantInfo.user_role;
} else if (errorData.tenant_lookup_required) {
  // Couldn't find tenant, treat as system error
  errorData.tenant_id = null;
  errorData.severity = 'high';
  errorData.category = 'orphaned_error';
}

delete errorData.tenant_lookup_required;
return { json: errorData };

