// Track error metrics for analytics
const errorData = $input.item.json;

// Create metrics for this error event
const metrics = [
  {
    tenant_id: errorData.tenant_id,
    user_id: errorData.user_id,
    metric_type: 'error_tracking',
    metric_name: 'error_occurred',
    value: 1,
    dimensions: {
      severity: errorData.severity,
      category: errorData.category,
      source: errorData.source,
      error_type: errorData.error_message.split(':')[0] || 'unknown'
    }
  },
  {
    tenant_id: errorData.tenant_id,
    metric_type: 'system_health',
    metric_name: `${errorData.severity}_error_count`,
    value: 1,
    dimensions: {
      source: errorData.source,
      category: errorData.category
    }
  }
];

// Add tenant-specific metrics if tenant exists
if (errorData.tenant_id) {
  metrics.push({
    tenant_id: errorData.tenant_id,
    metric_type: 'tenant_health',
    metric_name: 'error_rate',
    value: 1,
    dimensions: {
      tenant_slug: errorData.tenant_slug,
      severity: errorData.severity
    }
  });
}

return { json: { metrics, alert_data: errorData } };

// Prepare comprehensive alert response
const alertData = $node['Prepare Error Metrics'].json.alert_data;
const metricsInserted = $node['Insert Error Metrics'].json;

const response = {
  success: true,
  message: 'Error alert processed and logged successfully',
  data: {
    alert_id: alertData.alert_id,
    severity: alertData.severity,
    category: alertData.category,
    tenant_id: alertData.tenant_id,
    escalated: alertData.escalate,
    notifications_sent: {
      slack: true,
      email: alertData.severity === 'critical',
      discord: alertData.severity === 'critical'
    },
    metrics_tracked: metricsInserted?.length || 0
  },
  processing: {
    workflow_execution_id: $execution.id,
    processed_at: new Date().toISOString(),
    processing_time_ms: Date.now() - new Date(alertData.timestamp).getTime()
  },
  next_steps: {
    monitor_resolution: true,
    track_pattern: alertData.category !== 'general',
    escalate_if_recurring: alertData.severity === 'high',
    investigate_immediately: alertData.severity === 'critical'
  }
};

return { json: response };
