// Enhanced input validation for multi-tenant registration
const input = $input.item.json;

// Required fields validation
if (!input.email || !input.password) {
  throw new Error('Email and password are required');
}

// Email format validation
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(input.email)) {
  throw new Error('Invalid email format');
}

// Password strength validation
if (input.password.length < 8) {
  throw new Error('Password must be at least 8 characters');
}

// Tenant information handling
const isFirstUser = !input.tenant_slug && !input.tenant_id;
const tenantSlug = input.tenant_slug || input.email.split('@')[1].replace(/\./g, '-').toLowerCase();

// Determine if this is a new tenant creation or existing tenant join
const registrationType = isFirstUser ? 'new_tenant' : 'existing_tenant';

return {
  json: {
    ...input,
    tenant_slug: tenantSlug,
    registration_type: registrationType,
    first_name: input.first_name || '',
    last_name: input.last_name || '',
    timezone: input.timezone || 'UTC',
    locale: input.locale || 'en',
    validated_at: new Date().toISOString()
  }
};

// Hash password using bcrypt
const bcrypt = require('bcrypt');
const saltRounds = 12;

const hashedPassword = bcrypt.hashSync($input.item.json.password, saltRounds);

// Prepare user data
const userData = {
  email: $input.item.json.email,
  password_hash: hashedPassword,
  first_name: $input.item.json.first_name,
  last_name: $input.item.json.last_name,
  timezone: $input.item.json.timezone,
  locale: $input.item.json.locale,
  email_verified: false,
  status: 'pending',
  created_at: new Date().toISOString()
};

// Pass through tenant information
const tenantInfo = $node['Check Existing Tenant'].json.length > 0 
  ? $node['Check Existing Tenant'].json[0]
  : $node['Create New Tenant'].json[0];

return {
  json: {
    ...userData,
    tenant_info: tenantInfo,
    registration_type: $input.item.json.registration_type
  }
};

// Create audit log entry
const userInfo = $node['Insert User'].json[0];
const tenantInfo = $input.item.json.tenant_info;
const linkInfo = $node['Link User to Tenant'].json[0];

const auditData = {
  tenant_id: tenantInfo.id,
  user_id: userInfo.id,
  action: 'CREATE',
  resource_type: 'user_registration',
  resource_id: userInfo.id,
  new_values: {
    user_id: userInfo.id,
    email: userInfo.email,
    tenant_id: tenantInfo.id,
    tenant_slug: tenantInfo.slug,
    registration_type: $input.item.json.registration_type
  },
  ip_address: $node['Registration Webhook'].json.headers['x-forwarded-for'] || $node['Registration Webhook'].json.headers['x-real-ip'] || 'unknown',
  user_agent: $node['Registration Webhook'].json.headers['user-agent'] || 'unknown',
  metadata: {
    workflow_id: $workflow.id,
    execution_id: $execution.id,
    timestamp: new Date().toISOString()
  }
};

return { json: auditData };

// Track registration metrics
const userInfo = $node['Insert User'].json[0];
const tenantInfo = $input.item.json.tenant_info;
const registrationType = $input.item.json.registration_type;

// Prepare metrics data
const metrics = [
  {
    tenant_id: tenantInfo.id,
    user_id: userInfo.id,
    metric_type: 'user_registration',
    metric_name: 'new_user_signup',
    value: 1,
    dimensions: {
      registration_type: registrationType,
      tenant_slug: tenantInfo.slug,
      user_timezone: $input.item.json.timezone,
      user_locale: $input.item.json.locale
    }
  },
  {
    tenant_id: tenantInfo.id,
    metric_type: 'tenant_activity',
    metric_name: registrationType === 'new_tenant' ? 'tenant_created' : 'user_added_to_tenant',
    value: 1,
    dimensions: {
      tenant_slug: tenantInfo.slug,
      action: 'user_registration'
    }
  }
];

return { json: { metrics } };

// Prepare final response
const userInfo = $node['Insert User'].json[0];
const tenantInfo = $node['Hash Password & Prepare Data'].json.tenant_info;
const linkInfo = $node['Link User to Tenant'].json[0];
const onboardingInfo = $node['Initialize Onboarding'].json[0];

const response = {
  success: true,
  message: 'User registered successfully',
  data: {
    user: {
      id: userInfo.id,
      email: userInfo.email,
      first_name: userInfo.first_name,
      last_name: userInfo.last_name,
      created_at: userInfo.created_at
    },
    tenant: {
      id: tenantInfo.id,
      name: tenantInfo.name,
      slug: tenantInfo.slug
    },
    role: linkInfo.role,
    onboarding: {
      id: onboardingInfo.id,
      current_step: onboardingInfo.current_step,
      checklist: onboardingInfo.checklist
    }
  },
  next_steps: {
    verify_email: true,
    complete_onboarding: true,
    setup_profile: true
  },
  metadata: {
    registration_type: $node['Validate & Process Input'].json.registration_type,
    workflow_execution_id: $execution.id,
    timestamp: new Date().toISOString()
  }
};

return { json: response };
